func 转换字节集并补齐32位(文本 string) []byte {

	转换后字节集 := []byte(文本)
	返回 := make([]byte, 32)
	copy(返回, 转换后字节集)
	for i := len(转换后字节集); i < 32; i++ {
		返回[i] = 0x20
	}
	return 返回
}

func 寻找字节集(原始字节集 []byte, 欲寻找的字节集 []byte) int {
	局_位置 := -1
	if len(欲寻找的字节集) == 0 {
		return 局_位置
	}
	局_长度 := len(欲寻找的字节集)
	for i := 0; i < (len(原始字节集) - 局_长度); i++ {
		if 原始字节集[i] == 欲寻找的字节集[0] {
			for a := 0; a < 局_长度; a++ {
				if 原始字节集[i+a] != 欲寻找的字节集[a] {
					break
				}
				if a == 局_长度 {
					局_位置 = i
				}
			}

			if 局_位置 != -1 {
				break
			}

		}
	}

	return 局_位置
}

// 校验值
func GetCheckValue(data []byte) []byte {
	keyLen := 4
	key := make([]byte, keyLen)
	dataLen := len(data)
	i := 0

	for i < dataLen {
		key[i%keyLen] ^= data[i]
		i++
	}

	return key
}
